var _services = require("@services");

class ProductController {
  constructor() {
    this.ProductService = _services.ProductService;
  }

  async getProductList(req, res, next) {
    try {
      let filter = {};

      if (req.query) {
        if (req.query.ids) {
          // get product by id list of shopping
          const {
            ids
          } = req.query;
          const queryIds = ids ? ids.split(",") : null;

          if (queryIds && queryIds.length) {
            filter = {
              where: {
                id: queryIds
              }
            };
          }
        } else if (req.query.page) {
          // get product by pagination
          const {
            page
          } = req.query;
          const offset = page * 10;
          filter = {
            offset,
            limit: 10
          };
        }
      }

      const productList = await _services.ProductService.getAllProduct(filter);
      const count = await _services.ProductService.getCount();
      return res.json({
        success: true,
        count,
        productList
      });
    } catch (err) {
      next(err);
    }
  } // - Get product list by filter


  async getProductListByFilter(req, res, next) {
    try {
      // - filter by cateogry and price
      let filter = {};
      const {
        filtertype
      } = req.headers;

      if (filtertype) {
        const filterPriceValue = this.filterProductByPrice(filtertype);
        const filterCategoryValue = this.filterProductByCategory(filtertype);

        if (filterPriceValue) {
          filter = {
            order: [["price", filterPriceValue]]
          };
        } else if (filterCategoryValue) {
          filter = {
            where: {
              category: +filterCategoryValue
            }
          };
        }
      }

      const results = await _services.ProductService.getProductByFilter(filter);
      return res.json({
        success: true,
        results
      });
    } catch (err) {
      next(err);
    }
  }

  filterProductByCategory(value) {
    switch (value) {
      case "1":
        return "1";

      case "2":
        return "2";

      case "3":
        return "3";

      default:
        return null;
    }
  }

  filterProductByPrice(value) {
    switch (value) {
      case "4":
        return "ASC";

      case "5":
        return "DESC";

      default:
        return null;
    }
  }

  async getProduct(req, res, next) {
    try {
      const {
        id
      } = req.params;
      const product = await _services.ProductService.getProduct(id);
      res.json({
        success: true,
        result: product
      });
    } catch (err) {
      next(err);
    }
  }

  async getCategoryList(req, res, next) {
    try {
      const {
        category
      } = req.params;
      const categoryList = await _services.ProductService.getCategoryList(category);
      res.json({
        success: true,
        result: categoryList
      });
    } catch (err) {
      next(err);
    }
  }

  async createProduct(req, res, next) {
    try {
      const params = req.body || null;
      const {
        name,
        price,
        color,
        category,
        currency
      } = req.body; // const imageUrl = req.file.buffer;

      let imageUrl = "";
      req.files.forEach((file, index) => {
        if (index === 0) {
          imageUrl = file.filename;
        } else {
          imageUrl = imageUrl + "," + file.filename;
        }
      });
      console.log("imageUrl: ", imageUrl);
      const result = await this.ProductService.create({
        name,
        imageUrl,
        price,
        color,
        category,
        currency
      });
      res.json({
        success: true,
        result: result
      });
    } catch (err) {
      next(err);
    }
  }

  async searchProduct(req, res, next) {
    try {
      const {
        params
      } = req.body;
      const assets = await _services.ProductService.search(params);
      return res.json({
        success: true,
        assets: assets
      });
    } catch (err) {
      next(err);
    }
  }

  async deleteProduct(req, res, next) {
    try {
      const {
        id
      } = req.body;
      await _services.ProductService.delete(id); // const results = await ProductService.getAllProduct();

      return res.json({
        success: true
      });
    } catch (err) {
      next(err);
    }
  }

  async customProduct(req, res, next) {
    try {
      const {
        id,
        name,
        price,
        color,
