secureRoutes.use("/products", ProductRoutes);
secureRoutes.use("/categories", CategoriesRoutes);
secureRoutes.use("/users", UserRoutes);
secureRoutes.use("/checkout", CheckOutRoutes);

export { publicRoutes, secureRoutes };

